import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
//import { Textarea } from "@/components/ui/textarea";
import Textarea from "@/components/ui/Textarea";

import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useToast } from "@/hooks/use-toast";
import { Mail, MapPin, Phone } from "lucide-react";
import { SiInstagram, SiWhatsapp } from "react-icons/si";

const contactSchema = z.object({
  name: z.string().min(2, "Name is required"),
  email: z.string().email("Invalid email address"),
  message: z.string().min(10, "Message should be at least 10 characters"),
});

type ContactForm = z.infer<typeof contactSchema>;

export default function Contact() {
  const { toast } = useToast();
  const form = useForm<ContactForm>({
    resolver: zodResolver(contactSchema),
  });

  const onSubmit = async (data: ContactForm) => {
    toast({
      title: "Message Sent",
      description: "Thank you for reaching out! Weâ€™ll get back to you soon.",
    });
    form.reset();
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8 text-center">Contact Us</h1>

      <div className="grid md:grid-cols-2 gap-8">
        {/* Contact Form Section */}
        <div>
          <h2 className="text-xl font-semibold mb-4">Send us a message</h2>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <div className="space-y-2">
              <label>Name</label>
              <Input {...form.register("name")} />
              {form.formState.errors.name && (
                <p className="text-sm text-red-500">{form.formState.errors.name.message}</p>
              )}
            </div>

            <div className="space-y-2">
              <label>Email</label>
              <Input {...form.register("email")} type="email" />
              {form.formState.errors.email && (
                <p className="text-sm text-red-500">{form.formState.errors.email.message}</p>
              )}
            </div>

            <div className="space-y-2">
              <label>Message</label>
              <Textarea {...form.register("message")} className="min-h-[150px]" />
              {form.formState.errors.message && (
                <p className="text-sm text-red-500">{form.formState.errors.message.message}</p>
              )}
            </div>

            <Button type="submit">Send Message</Button>
          </form>
        </div>

        {/* Contact Details Section */}
        <div>
          <h2 className="text-xl font-semibold mb-4">Contact Information</h2>
          <Card>
            <CardContent className="pt-6 space-y-4">
              <div className="flex items-center gap-3">
                <MapPin className="h-5 w-5" />
                <div>
                  <h3 className="font-medium">Address</h3>
                  <p className="text-sm text-gray-600">
                    Brookfield, Bangalore, 560037
                  </p>
                </div>
              </div>

              <div className="flex items-center gap-3">
                <Phone className="h-5 w-5" />
                <div>
                  <h3 className="font-medium">Phone</h3>
                  <p className="text-sm text-gray-600">+91 7259770330</p>
                </div>
              </div>

              <div className="flex items-center gap-3">
                <SiWhatsapp className="h-5 w-5 text-green-500" />
                <div>
                  <h3 className="font-medium">WhatsApp</h3>
                  <p className="text-sm text-gray-600">+91 7259770330</p>
                </div>
              </div>

              <div className="flex items-center gap-3">
                <SiInstagram className="h-5 w-5 text-pink-500" />
                <div>
                  <h3 className="font-medium">Instagram</h3>
                  <p className="text-sm text-gray-600">@trailriders.in</p>
                </div>
              </div>

              <div className="flex items-center gap-3">
                <Mail className="h-5 w-5" />
                <div>
                  <h3 className="font-medium">Email</h3>
                  <p className="text-sm text-gray-600">bikers@trailriders.in</p>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Business Hours Section */}
          <h2 className="text-xl font-semibold mt-8 mb-4">Business Hours</h2>
          <Card>
            <CardContent className="pt-6">
              <div className="space-y-2">
                <div className="flex justify-between">
                  <span>Monday - Friday</span>
                  <span>9:00 AM - 6:00 PM</span>
                </div>
                <div className="flex justify-between">
                  <span>Saturday</span>
                  <span>10:00 AM - 4:00 PM</span>
                </div>
                <div className="flex justify-between">
                  <span>Sunday</span>
                  <span>Closed</span>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}
