import React from "react";
import { Link, useLocation } from "react-router-dom";
import { Link as ScrollLink } from "react-scroll";
import "../styles/Navbar.css";

const Navbar = () => {
  const location = useLocation();  // Get current route path
  const isHomePage = location.pathname === "/";  // Check if on home page

  return (
    <nav className="navbar">
      <div className="logo">Trail Riders</div>
      <ul className="nav-links">
        <li><Link to="/">Home</Link></li>

        {/* Use react-scroll only on home page, otherwise use normal Link */}
        <li>
          {isHomePage ? (
            <ScrollLink to="events" smooth={true} duration={500}>Events</ScrollLink>
          ) : (
            <Link to="/#events">Events</Link>
          )}
        </li>

        <li>
          {isHomePage ? (
            <ScrollLink to="rides" smooth={true} duration={500}>Rides</ScrollLink>
          ) : (
            <Link to="/#rides">Rides</Link>
          )}
        </li>

        <li>
          {isHomePage ? (
            <ScrollLink to="blogs" smooth={true} duration={500}>Blogs</ScrollLink>
          ) : (
            <Link to="/#blogs">Blogs</Link>
          )}
        </li>

        <li>
          {isHomePage ? (
            <ScrollLink to="gallery" smooth={true} duration={500}>Gallery</ScrollLink>
          ) : (
            <Link to="/#gallery">Gallery</Link>
          )}
        </li>

        <li>
          {isHomePage ? (
            <ScrollLink to="shop" smooth={true} duration={500}>Shop</ScrollLink>
          ) : (
            <Link to="/#shop">Shop</Link>
          )}
        </li>

        <li>
          {isHomePage ? (
            <ScrollLink to="contact" smooth={true} duration={500}>Contact</ScrollLink>
          ) : (
            <Link to="/#contact">Contact</Link>
          )}
        </li>
      </ul>

      <div className="auth-buttons">
        <Link to="/login" className="login-btn">Login</Link>
        <Link to="/signup" className="signup-btn">Signup</Link>
      </div>
    </nav>
  );
};

export default Navbar;
