import React, { useEffect, useState } from "react";

const Events = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    fetch("/events.json")
      .then((response) => response.json())
      .then((data) => setEvents(data))
      .catch((error) => console.error("Error fetching events:", error));
  }, []);

  return (
    <section id="events" style={{ textAlign: "center", padding: "40px" }}>
      <h2>Upcoming Events</h2>
      {events.length === 0 ? (
        <p>Stay tuned for upcoming biking events!</p>
      ) : (
        <div style={{ display: "grid", gap: "20px", justifyContent: "center" }}>
          {events.map((event, index) => (
            <div key={index} style={{ maxWidth: "400px", margin: "auto", textAlign: "left" }}>
              <img
                src={event.image}
                alt={event.title}
                style={{ width: "100%", borderRadius: "8px", objectFit: "cover" }}
              />
              <div style={{ padding: "10px" }}>
                <h3>{event.title}</h3>
                <p><strong>Date:</strong> {event.date}</p>
                <p><strong>Time:</strong> {event.time}</p>
                <p><strong>Location:</strong> {event.location}</p>
              </div>
            </div>
          ))}
        </div>
      )}
    </section>
  );
};

export default Events;
