import React from "react";
import { useNavigate } from "react-router-dom";
import "../styles/Rides.css";

const ridesData = [
  { id: 1, title: "Sunset Coastal Ride", date: "March 10, 2025", time: "5:00 PM", location: "Marine Drive, Mumbai", image: "/assets/kerala-ride.jpg" },
];

const Rides = () => {
  const navigate = useNavigate();

  return (
    <section className="rides-container">
      <h2>Upcoming Rides</h2>
      <div className="rides-grid">
        {ridesData.map((ride) => (
          <div key={ride.id} className="ride-card">
            <h3>{ride.title}</h3>
            <p><strong>Date:</strong> {ride.date}</p>
            <p><strong>Time:</strong> {ride.time}</p>
            <p><strong>Location:</strong> {ride.location}</p>
            <button onClick={() => navigate(`/join-ride/${ride.id}`)}>Join Ride</button>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Rides;
